// @flow strict
import * as React from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import borders from './Borders.css';
import styles from './Touchable.css';
import { fromClassName, identity, toProps, type Style } from './style.js';
import { bind, range } from './transforms.js';

type MouseCursor =
  | 'copy'
  | 'grab'
  | 'grabbing'
  | 'move'
  | 'noDrop'
  | 'pointer'
  | 'zoomIn'
  | 'zoomOut';
type Rounding = 0 | 1 | 2 | 3 | 4 | 5 | 6 | 7 | 8 | 'circle' | 'pill';

type Props = {|
  children?: React.Node,
  fullHeight?: boolean,
  fullWidth?: boolean,
  mouseCursor?: MouseCursor,
  onBlur?: ({ event: SyntheticFocusEvent<HTMLDivElement> }) => void,
  onFocus?: ({ event: SyntheticFocusEvent<HTMLDivElement> }) => void,
  onMouseEnter?: ({ event: SyntheticMouseEvent<HTMLDivElement> }) => void,
  onMouseLeave?: ({ event: SyntheticMouseEvent<HTMLDivElement> }) => void,
  onTouch?: ({
    event:
      | SyntheticMouseEvent<HTMLDivElement>
      | SyntheticKeyboardEvent<HTMLDivElement>,
  }) => void,
  rounding?: Rounding,
|};

const SPACE_CHAR_CODE = 32;
const ENTER_CHAR_CODE = 13;

const RoundingPropType = PropTypes.oneOf([
  0,
  1,
  2,
  3,
  4,
  5,
  6,
  7,
  8,
  'circle',
  'pill',
]);

const getRoundingStyle = (r: Rounding): Style => {
  if (typeof r === 'number') {
    return bind(range('rounding'), borders)(r);
  }

  if (r === 'circle') {
    return fromClassName(borders.circle);
  }

  if (r === 'pill') {
    return fromClassName(borders.pill);
  }

  return identity();
};

export default class Touchable extends React.Component<Props> {
  static propTypes = {
    children: PropTypes.node,
    fullHeight: PropTypes.bool,
    fullWidth: PropTypes.bool,
    mouseCursor: PropTypes.oneOf([
      'copy',
      'grab',
      'grabbing',
      'move',
      'noDrop',
      'pointer',
      'zoomIn',
      'zoomOut',
    ]),
    onBlur: PropTypes.func,
    onFocus: PropTypes.func,
    onTouch: PropTypes.func,
    onMouseEnter: PropTypes.func,
    onMouseLeave: PropTypes.func,
    rounding: RoundingPropType,
  };

  handleKeyPress = (event: SyntheticKeyboardEvent<HTMLDivElement>) => {
    const { onTouch } = this.props;
    // Check to see if space or enter were pressed
    if (
      onTouch &&
      (event.charCode === SPACE_CHAR_CODE || event.charCode === ENTER_CHAR_CODE)
    ) {
      // Prevent the default action to stop scrolling when space is pressed
      event.preventDefault();
      onTouch({ event });
    }
  };

  handleBlur = (event: SyntheticFocusEvent<HTMLDivElement>) => {
    const { onBlur } = this.props;
    if (onBlur) {
      onBlur({ event });
    }
  };

  handleFocus = (event: SyntheticFocusEvent<HTMLDivElement>) => {
    const { onFocus } = this.props;
    if (onFocus) {
      onFocus({ event });
    }
  };

  handleMouseEnter = (event: SyntheticMouseEvent<HTMLDivElement>) => {
    const { onMouseEnter } = this.props;
    if (onMouseEnter) {
      onMouseEnter({ event });
    }
  };

  handleMouseLeave = (event: SyntheticMouseEvent<HTMLDivElement>) => {
    const { onMouseLeave } = this.props;
    if (onMouseLeave) {
      onMouseLeave({ event });
    }
  };

  handleClick = (event: SyntheticMouseEvent<HTMLDivElement>) => {
    const { onTouch } = this.props;
    if (onTouch) {
      onTouch({ event });
    }
  };

  render() {
    const {
      children,
      fullWidth = true,
      fullHeight,
      mouseCursor = 'pointer',
      rounding = 0,
    } = this.props;

    const classes = classnames(
      styles.touchable,
      styles[mouseCursor],
      toProps(getRoundingStyle(rounding)).className,
      {
        [styles.fullHeight]: fullHeight,
        [styles.fullWidth]: fullWidth,
      }
    );

    return (
      <div
        className={classes}
        onClick={this.handleClick}
        onBlur={this.handleBlur}
        onFocus={this.handleFocus}
        onMouseEnter={this.handleMouseEnter}
        onMouseLeave={this.handleMouseLeave}
        onKeyPress={this.handleKeyPress}
        role="button"
        tabIndex="0"
      >
        {children}
      </div>
    );
  }
}

// @flow strict
import * as React from 'react';
import Box from './Box.js';
import Icon from './Icon.js';
import TableHeaderCell from './TableHeaderCell.js';
import Touchable from './Touchable.js';

type Props = {|
  children: React.Node,
  colSpan?: number,
  onSortChange: ({
    event:
      | SyntheticMouseEvent<HTMLDivElement>
      | SyntheticKeyboardEvent<HTMLDivElement>,
  }) => void,
  rowSpan?: number,
  scope?: 'col' | 'colgroup' | 'row' | 'rowgroup',
  sortOrder: 'asc' | 'desc',
  status: 'active' | 'inactive',
|};

export default function TableSortableHeaderCell(props: Props) {
  const {
    children,
    colSpan,
    scope,
    rowSpan,
    status,
    sortOrder,
    onSortChange,
  } = props;

  const [isFocused, setFocused] = React.useState(false);
  const [isHovered, setHovered] = React.useState(false);

  const shouldShowIcon = status === 'active' || isHovered || isFocused;
  const visibility = shouldShowIcon ? 'visible' : 'hidden';

  return (
    <TableHeaderCell colSpan={colSpan} rowSpan={rowSpan} scope={scope}>
      <Touchable
        onTouch={onSortChange}
        onMouseEnter={() => setHovered(true)}
        onMouseLeave={() => setHovered(false)}
        onFocus={() => setFocused(true)}
        onBlur={() => setFocused(false)}
      >
        <Box display="flex">
          {children}
          <Box
            marginLeft={2}
            dangerouslySetInlineStyle={{
              __style: { visibility },
            }}
          >
            <Icon
              accessibilityLabel=""
              icon={
                status === 'active' && sortOrder === 'asc'
                  ? 'sort-ascending'
                  : 'sort-descending'
              }
              color={status === 'active' ? 'darkGray' : 'gray'}
            />
          </Box>
        </Box>
      </Touchable>
    </TableHeaderCell>
  );
}

